function initFileNames(){[].slice.call(document.querySelectorAll(".project-detail[data-file]")).forEach(e=>{let t=e.dataset.file.split("/");e.querySelector(".project-detail__value").innerText=t[t.length-1]})}function initResultTable(){const e=document.querySelector(".results-table");if(!e)return;const t=document.querySelector(".do-displayCompact");t.addEventListener("click",()=>{t.classList.contains("is-active")?(t.classList.remove("is-active"),e.classList.remove("results-table__compact")):(t.classList.add("is-active"),e.classList.add("results-table__compact"))});const s=document.querySelector(".do-displayRelevant"),l=[].slice.call(document.querySelectorAll(".results-table tbody tr"));s.addEventListener("keyup",()=>{const e=parseFloat(s.value)||1;l.forEach(t=>{t.classList.remove("is-hidden");const s=t.querySelector(".results-table__p");s?parseFloat(s.innerText)<=e||t.classList.add("is-hidden"):t.previousElementSibling.classList.contains("is-hidden")&&t.classList.add("is-hidden")})})}document.addEventListener("DOMContentLoaded",()=>{initFileNames(),initResultTable()});